mapper.insert:

INSERT INTO nurse /* QueryID : com.demo.proworks.nurse.insertNurse */
( 
nurse_id
 , position 
 , special_note 
 , created_at 
 , updated_at 
 , hire_date 
 , ward_id 
 , carrer 
)
VALUES  
( 
#{empno}
 , #{position} 
 , #{specialNote} 
 , STR_TO_DATE(#{createdAt}, '%Y-%m-%d') 
 , STR_TO_DATE(#{updatedAt}, '%Y-%m-%d') 
 , STR_TO_DATE(#{hireDate}, '%Y-%m-%d') 
 , #{wardId} 
 , #{carrer} 
)

mapper.update:

UPDATE nurse    /* QueryID : com.demo.proworks.nurse.updateNurse */      

     , position = #{position} 
     , special_note = #{specialNote} 
     , updated_at = STR_TO_DATE(#{updatedAt}, '%Y-%m-%d') 
     , hire_date = STR_TO_DATE(#{hireDate}, '%Y-%m-%d') 
     , ward_id = #{wardId} 
     , carrer = #{carrer} 

WHERE   nurse_id = #{nurseId}

mapper.select:
SELECT /* QueryID : com.demo.proworks.nurse.selectListNurse */ n.nurse_id AS nurseId, e.empno AS empno, e.ename AS nurseName, n.position, n.special_note AS specialNote, DATE_FORMAT(n.created_at, '%Y-%m-%d') AS createdAt, DATE_FORMAT(n.updated_at, '%Y-%m-%d') AS updatedAt, DATE_FORMAT(n.hire_date, '%Y-%m-%d') AS hireDate, n.ward_id AS wardId, n.carrer, w.ward_name AS wardName FROM ( SELECT S.*, CEIL( (@ROWNUM:=@ROWNUM+1) / #{pageSize} ) AS PAGE FROM ( SELECT n.*, w.ward_name, e.ename FROM nurse n LEFT JOIN emp e ON n.nurse_id = e.empno LEFT JOIN ward w ON n.ward_id = w.ward_id WHERE 1=1
AND n.nurse_id = #{scNurseId}
AND e.ename LIKE CONCAT('%',#{scNurseName},'%')
AND n.position LIKE CONCAT('%',#{scPosition},'%')
AND n.ward_id = #{scWardId}
ORDER BY n.nurse_id ASC ) S, (SELECT @ROWNUM := 0) TMP ) A LEFT JOIN emp e ON A.nurse_id = e.empno LEFT JOIN ward w ON A.ward_id = w.ward_id WHERE PAGE = #{pageIndex}

SELECT /* QueryID : com.demo.proworks.nurse.selectListCountNurse */ COUNT(*) totcnt FROM nurse n LEFT JOIN emp e ON n.nurse_id = e.empno LEFT JOIN ward w ON n.ward_id = w.ward_id WHERE 1=1
AND n.nurse_id = #{scNurseId}
AND e.ename LIKE CONCAT('%',#{scNurseName},'%')
AND n.position LIKE CONCAT('%',#{scPosition},'%')
AND n.ward_id = #{scWardId}
