scwin.toggleSidebar = function() {
	console.log("ÔøΩÔøΩÔøΩÔøΩÔøΩÔøΩ ÌñÑÎ≤ÑÍ±∞ ÏïÑÏù¥ÏΩò ÌÅ¥Î¶≠Îê® - ÏÇ¨Ïù¥ÎìúÎ∞î ÌÜ†Í∏Ä ÏãúÎèÑ");

	var success = false;

	// Î∞©Î≤ï 1: Î∂ÄÎ™® ÌîÑÎ†àÏûÑÏóêÏÑú ÏßÅÏ†ë Ìï®Ïàò Ìò∏Ï∂ú
	try {
		if (window.parent && window.parent.scwin && window.parent.scwin.toggleSidebar) {
			window.parent.scwin.toggleSidebar();
			console.log("‚úÖ Î∞©Î≤ï 1 ÏÑ±Í≥µ: parent.scwin.toggleSidebar");
			success = true;
		}
	} catch (e) {
		console.log("Î∞©Î≤ï 1 Ïã§Ìå®:", e);
	}

	// Î∞©Î≤ï 2: Ï†ÑÏó≠ Ìï®ÏàòÎ°ú Îì±Î°ùÎêú Ìï®Ïàò Ìò∏Ï∂ú
	if (!success) {
		try {
			if (window.parent && window.parent.toggleSidebarFromHeader) {
				window.parent.toggleSidebarFromHeader();
				console.log("‚úÖ Î∞©Î≤ï 2 ÏÑ±Í≥µ: parent.toggleSidebarFromHeader");
				success = true;
			}
		} catch (e) {
			console.log("Î∞©Î≤ï 2 Ïã§Ìå®:", e);
		}
	}

	// Î∞©Î≤ï 3: ÏµúÏÉÅÏúÑ ÌîÑÎ†àÏûÑÏóêÏÑú Ìï®Ïàò Ï∞æÍ∏∞
	if (!success) {
		try {
			if (window.top && window.top.scwin && window.top.scwin.toggleSidebar) {
				window.top.scwin.toggleSidebar();
				console.log("‚úÖ Î∞©Î≤ï 3 ÏÑ±Í≥µ: top.scwin.toggleSidebar");
				success = true;
			}
		} catch (e) {
			console.log("Î∞©Î≤ï 3 Ïã§Ìå®:", e);
		}
	}

	// Î∞©Î≤ï 4: postMessage ÏÇ¨Ïö©
	if (!success) {
		try {
			window.parent.postMessage({
				action: 'toggleSidebar',
				source: 'header'
			}, '*');
			console.log("‚úÖ Î∞©Î≤ï 4 ÏÑ±Í≥µ: postMessage Ï†ÑÏÜ°");
			success = true;
		} catch (e) {
			console.log("Î∞©Î≤ï 4 Ïã§Ìå®:", e);
		}
	}

	if (!success) {
		console.error("‚ùå Î™®Îì† ÌÜµÏã† Î∞©Î≤ï Ïã§Ìå®");
		alert("ÏÇ¨Ïù¥ÎìúÎ∞î ÌÜ†Í∏Ä Í∏∞Îä•Ïóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. ÌéòÏù¥ÏßÄÎ•º ÏÉàÎ°úÍ≥†Ïπ®Ìï¥Ï£ºÏÑ∏Ïöî.");
	}
};

