scwin.updateHamburgerIcon = function(isVisible) {
	try {
		var hamburgerMenu = document.querySelector('.hamburger-menu');
		if (hamburgerMenu) {
			if (isVisible) {
				// ÏÇ¨Ïù¥ÎìúÎ∞îÍ∞Ä Ïó¥Î†§ÏûàÏùÑ Îïå ‚Üí X Î™®Ïñë Ïú†ÏßÄ (collapsed ÌÅ¥ÎûòÏä§ Ï†úÍ±∞)
				hamburgerMenu.classList.remove('collapsed');
				console.log("‚úÖ ÌñÑÎ≤ÑÍ±∞ ÏïÑÏù¥ÏΩò: X Î™®Ïñë (ÏÇ¨Ïù¥ÎìúÎ∞î Ïó¥Î¶º)");
			} else {
				// ÏÇ¨Ïù¥ÎìúÎ∞îÍ∞Ä Îã´ÌòÄÏûàÏùÑ Îïå ‚Üí ÏùºÎ∞ò ÌñÑÎ≤ÑÍ±∞ ÏïÑÏù¥ÏΩò (collapsed ÌÅ¥ÎûòÏä§ Ï∂îÍ∞Ä)
				hamburgerMenu.classList.add('collapsed');
				console.log("‚úÖ ÌñÑÎ≤ÑÍ±∞ ÏïÑÏù¥ÏΩò: ÏùºÎ∞ò Î™®Ïñë (ÏÇ¨Ïù¥ÎìúÎ∞î Îã´Ìûò)");
			}
		}
	} catch (e) {
		console.error("ÌñÑÎ≤ÑÍ±∞ ÏïÑÏù¥ÏΩò ÏóÖÎç∞Ïù¥Ìä∏ Ïò§Î•ò:", e);
	}
};

scwin.toggleNotifications = function() {
	console.log("ÏïåÎ¶º ÌÜ†Í∏Ä Ìï®Ïàò Ìò∏Ï∂ú");

	// Îã§Î•∏ Î©îÎâ¥ Î®ºÏ†Ä Îã´Í∏∞
