	}
};

scwin.showNotificationDropdown = function() {
	console.log("ÏïåÎ¶º ÎìúÎ°≠Îã§Ïö¥ ÌëúÏãú ÏãúÎèÑ");

	try {
		// Ï†ÑÏó≠ jQuery ÏÇ¨Ïö© ÏãúÎèÑ
		if (typeof $ !== 'undefined') {
			$('.notification-dropdown').addClass('show');
			console.log("jQueryÎ°ú ÏïåÎ¶º ÎìúÎ°≠Îã§Ïö¥ ÌëúÏãúÎê®");
		} else if (typeof window.$ !== 'undefined') {
			window.$('.notification-dropdown').addClass('show');
			console.log("window.jQueryÎ°ú ÏïåÎ¶º ÎìúÎ°≠Îã§Ïö¥ ÌëúÏãúÎê®");
		} else {
			// ÏàúÏàò JavaScript ÏÇ¨Ïö©
			var elements = document.querySelectorAll('.notification-dropdown');
			for (var i = 0; i < elements.length; i++) {
				elements[i].classList.add('show');
			}
			console.log("ÏàúÏàò JavaScriptÎ°ú ÏïåÎ¶º ÎìúÎ°≠Îã§Ïö¥ ÌëúÏãúÎê®");
		}
		scwin.notificationVisible = true;
	} catch (e) {
		console.log("ÏïåÎ¶º ÎìúÎ°≠Îã§Ïö¥ ÌëúÏãú Ïò§Î•ò:", e);
	}
};

