head.script:
scwin.sidebarVisible = false;
scwin.notificationVisible = false;
scwin.userMenuVisible = false;

scwin.onpageload = function() {
	console.log("헤더 초기화 완료");
	// 초기 상태 설정
	scwin.hideNotificationDropdown();
	scwin.hideUserMenu();
};

scwin.toggleSidebar = function() {
	console.log("사이드바 토글");
	scwin.sidebarVisible = !scwin.sidebarVisible;

	if (scwin.sidebarVisible) {
		alert("사이드바가 열렸습니다.");
	} else {
		alert("사이드바가 닫혔습니다.");
	}
};

scwin.toggleNotifications = function() {
	console.log("알림 토글 함수 호출");

	// 다른 메뉴 먼저 닫기
	scwin.hideUserMenu();

	if (scwin.notificationVisible) {
		scwin.hideNotificationDropdown();
	} else {
		scwin.showNotificationDropdown();
	}
};

scwin.showNotificationDropdown = function() {
	console.log("알림 드롭다운 표시 시도");

	try {
		// 전역 jQuery 사용 시도
		if (typeof $ !== 'undefined') {
			$('.notification-dropdown').addClass('show');
			console.log("jQuery로 알림 드롭다운 표시됨");
		} else if (typeof window.$ !== 'undefined') {
			window.$('.notification-dropdown').addClass('show');
			console.log("window.jQuery로 알림 드롭다운 표시됨");
		} else {
			// 순수 JavaScript 사용
			var elements = document.querySelectorAll('.notification-dropdown');
			for (var i = 0; i < elements.length; i++) {
				elements[i].classList.add('show');
			}
			console.log("순수 JavaScript로 알림 드롭다운 표시됨");
		}
		scwin.notificationVisible = true;
	} catch (e) {
		console.log("알림 드롭다운 표시 오류:", e);
	}
};

scwin.hideNotificationDropdown = function() {
	console.log("알림 드롭다운 숨김 시도");

	try {
		// 전역 jQuery 사용 시도
		if (typeof $ !== 'undefined') {
			$('.notification-dropdown').removeClass('show');
			console.log("jQuery로 알림 드롭다운 숨김");
		} else if (typeof window.$ !== 'undefined') {
			window.$('.notification-dropdown').removeClass('show');
			console.log("window.jQuery로 알림 드롭다운 숨김");
		} else {
			// 순수 JavaScript 사용
			var elements = document.querySelectorAll('.notification-dropdown');
			for (var i = 0; i < elements.length; i++) {
				elements[i].classList.remove('show');
			}
			console.log("순수 JavaScript로 알림 드롭다운 숨김");
		}
		scwin.notificationVisible = false;
	} catch (e) {
		console.log("알림 드롭다운 숨김 오류:", e);
	}
};

scwin.toggleUserMenu = function() {
	console.log("사용자 메뉴 토글 함수 호출");

	// 다른 메뉴 먼저 닫기
	scwin.hideNotificationDropdown();

	if (scwin.userMenuVisible) {
		scwin.hideUserMenu();
	} else {
		scwin.showUserMenu();
	}
};

scwin.showUserMenu = function() {
	console.log("사용자 메뉴 표시 시도");

	try {
		// 전역 jQuery 사용 시도
		if (typeof $ !== 'undefined') {
			$('.user-dropdown').addClass('show');
			console.log("jQuery로 사용자 메뉴 표시됨");
		} else if (typeof window.$ !== 'undefined') {
			window.$('.user-dropdown').addClass('show');
			console.log("window.jQuery로 사용자 메뉴 표시됨");
		} else {
			// 순수 JavaScript 사용
			var elements = document.querySelectorAll('.user-dropdown');
			for (var i = 0; i < elements.length; i++) {
				elements[i].classList.add('show');
			}
			console.log("순수 JavaScript로 사용자 메뉴 표시됨");
		}
		scwin.userMenuVisible = true;
	} catch (e) {
		console.log("사용자 메뉴 표시 오류:", e);
	}
};

scwin.hideUserMenu = function() {
	console.log("사용자 메뉴 숨김 시도");

	try {
		// 전역 jQuery 사용 시도
		if (typeof $ !== 'undefined') {
			$('.user-dropdown').removeClass('show');
			console.log("jQuery로 사용자 메뉴 숨김");
		} else if (typeof window.$ !== 'undefined') {
			window.$('.user-dropdown').removeClass('show');
			console.log("window.jQuery로 사용자 메뉴 숨김");
		} else {
			// 순수 JavaScript 사용
			var elements = document.querySelectorAll('.user-dropdown');
			for (var i = 0; i < elements.length; i++) {
				elements[i].classList.remove('show');
			}
			console.log("순수 JavaScript로 사용자 메뉴 숨김");
		}
		scwin.userMenuVisible = false;
	} catch (e) {
		console.log("사용자 메뉴 숨김 오류:", e);
	}
};

scwin.logout = function() {
	if (confirm("로그아웃 하시겠습니까?")) {
		alert("로그아웃 됩니다.");
		scwin.hideUserMenu();
		// 여기에 실제 로그아웃 로직 추가
	}
};

// 외부 클릭 시 메뉴 닫기
scwin.closeMenus = function() {
	scwin.hideNotificationDropdown();
	scwin.hideUserMenu();
};
