head.script:
scwin.activeMenuId = null;

scwin.onpageload = function() {

    // DOMÏù¥ ÏôÑÏ†ÑÌûà Î°úÎìúÎêú ÌõÑ ÎîîÎ≤ÑÍπÖ Ï†ïÎ≥¥ Ï∂úÎ†•
    setTimeout(function() {
        scwin.debugAvailableMenuIds();

        // Î∂ÄÎ™® ÌîÑÎ†àÏûÑÏóêÏÑú ÌòÑÏû¨ ÌôúÏÑ± Î©îÎâ¥ Ï†ïÎ≥¥ ÌôïÏù∏
        if (window.parent && window.parent.scwin && window.parent.scwin.currentActiveMenuId) {
            var currentMenuId = window.parent.scwin.currentActiveMenuId;
            scwin.setActiveMenu(currentMenuId);
        } else {
            // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Ï≤´ Î≤àÏß∏ Î©îÎâ¥ ÌôúÏÑ±Ìôî
            scwin.setActiveMenu("menu_duty_manage");
        }
    }, 300);
};

// Î©îÎâ¥ ÌôúÏÑ±Ìôî ÏÑ§Ï†ï
scwin.setActiveMenu = function(menuId) {
    try {

        // Î™®Îì† Î©îÎâ¥ ÎπÑÌôúÏÑ±Ìôî
        var menuItems = document.querySelectorAll('.menu-item');
        for (var i = 0; i < menuItems.length; i++) {
            menuItems[i].classList.remove('active');
        }

        // Í∞ïÎ†•Ìïú Î©îÎâ¥ Ï∞æÍ∏∞ Ìï®Ïàò ÏÇ¨Ïö©
        var selectedMenu = scwin.findMenuElement(menuId);

        if (selectedMenu) {
            selectedMenu.classList.add('active');
            scwin.activeMenuId = menuId;

            // Î∂ÄÎ™® ÌîÑÎ†àÏûÑÏóê ÌòÑÏû¨ ÌôúÏÑ± Î©îÎâ¥ Ï†ïÎ≥¥ Ï†ÑÎã¨
            if (window.parent && window.parent.scwin) {
                window.parent.scwin.currentActiveMenuId = menuId;
            }
        } else {
            // ÏÇ¨Ïö© Í∞ÄÎä•Ìïú Î©îÎâ¥ IDÎì§ Ï∂úÎ†•
            scwin.debugAvailableMenuIds();
        }
    } catch (e) {
    }
};

// Í∞ïÎ†•Ìïú Î©îÎâ¥ ÏöîÏÜå Ï∞æÍ∏∞ Ìï®Ïàò
scwin.findMenuElement = function(menuId) {
    var element = null;

    // 1. Ï†ïÌôïÌïú ID Îß§Ïπ≠
    element = document.getElementById(menuId);
    if (element) {
        return element;
    }

    // 2. WebSquare Ï†ëÎëêÏÇ¨ Í≥†Î†§
    element = document.getElementById('mf_' + menuId);
    if (element) {
        return element;
    }

    // 3. querySelectorÎ°ú Í≤ÄÏÉâ
    element = document.querySelector('#' + menuId);
    if (element) {
        return element;
    }

    // 4. mf_ Ï†ëÎëêÏÇ¨ÏôÄ Ìï®Íªò querySelector Í≤ÄÏÉâ
    element = document.querySelector('#mf_' + menuId);
    if (element) {
        return element;
    }

    // 5. Î∂ÄÎ∂Ñ ID Îß§Ïπ≠ÏúºÎ°ú Í≤ÄÏÉâ
    element = document.querySelector('[id*="' + menuId + '"]');
    if (element) {
        return element;
    }

    // 6. ÎùºÎ≤® ÌÖçÏä§Ìä∏Î°ú Í≤ÄÏÉâ (ÎßàÏßÄÎßâ ÏàòÎã®)
    var menuItems = document.querySelectorAll('.menu-item');
    for (var i = 0; i < menuItems.length; i++) {
        var label = menuItems[i].querySelector('xf\\:label, label');
        if (label && label.textContent && label.textContent.trim() === getMenuLabel(menuId)) {
            return menuItems[i];
        }
    }

    return null;
};

// Î©îÎâ¥ IDÏóê Ìï¥ÎãπÌïòÎäî ÎùºÎ≤® ÌÖçÏä§Ìä∏ Î∞òÌôò
scwin.getMenuLabel = function(menuId) {
    var menuLabels = {
        'menu_duty_manage': 'Í∑ºÎ¨¥Ïûê Í¥ÄÎ¶¨',
        'menu_schedule_manage': 'Ïä§ÏºÄÏ§Ñ Í¥ÄÎ¶¨',
        'menu_my_schedule': 'ÎÇ¥ Ïä§ÏºÄÏ§Ñ Î≥¥Í∏∞',
        'menu_work_input': 'Ìù¨Îßù Í∑ºÎ¨¥ ÏûÖÎ†•'
    };
    return menuLabels[menuId] || '';
};

// ÏÇ¨Ïö© Í∞ÄÎä•Ìïú Î©îÎâ¥ ID ÎîîÎ≤ÑÍπÖ Ìï®Ïàò
scwin.debugAvailableMenuIds = function() {
    var menuItems = document.querySelectorAll('.menu-item');

    for (var i = 0; i < menuItems.length; i++) {
    }

    // Î™®Îì† IDÍ∞Ä ÏûàÎäî ÏöîÏÜåÎì§ÎèÑ Ï∂úÎ†•
    var allElementsWithId = document.querySelectorAll('[id]');
    for (var i = 0; i < Math.min(allElementsWithId.length, 20); i++) {
    }
};

// Î∂ÄÎ™® ÌîÑÎ†àÏûÑÍ≥ºÏùò ÌÜµÏã† Ìï®Ïàò (mkduty.xmlÏóêÏÑú ÏÑ§Ï†ïÎê®)
scwin.onMenuClick = null;

// Î©îÎâ¥ ÌÅ¥Î¶≠ Ï≤òÎ¶¨
scwin.handleMenuClick = function(menuId, menuUrl, menuTitle) {
    try {

        // Î®ºÏ†Ä Î©îÎâ¥ ÌôúÏÑ±Ìôî
        scwin.setActiveMenu(menuId);

        // Î∂ÄÎ™® ÌîÑÎ†àÏûÑÏóê Î©îÎâ¥ ÌÅ¥Î¶≠ Ïù¥Î≤§Ìä∏ Ï†ÑÎã¨
        if (scwin.onMenuClick) {
            scwin.onMenuClick(menuId, menuUrl, menuTitle);
        } else {
        }

        // ÌôúÏÑ± ÏÉÅÌÉú Ïú†ÏßÄÎ•º ÏúÑÌïú Ï∂îÍ∞Ä Ï≤òÎ¶¨
        setTimeout(function() {
            scwin.setActiveMenu(menuId);
        }, 100);

    } catch (e) {
    }
};

