// 부모 프레임과의 통신 설정
scwin.setupParentCommunication = function() {
    try {
        if (window.parent && window.parent.scwin) {
            // 부모에서 탭 추가 함수 호출할 수 있도록 연결
            window.parent.scwin.addTabToControl = scwin.addTab;
            window.parent.scwin.activateTabInControl = scwin.activateTab;
            window.parent.scwin.closeTabInControl = scwin.closeTab;
            console.log("✅ 부모 프레임과 탭 컨트롤 통신 설정 완료");
        }
    } catch (e) {
        console.error("부모 프레임 통신 설정 오류:", e);
    }
};

// 웹스퀘어 공식 API를 사용한 탭 추가
scwin.addTab = async function(tabId, tabTitle, tabUrl, isActive) {
    try {
        console.log("탭 추가:", tabId, tabTitle, tabUrl, isActive);

        // 탭 컨트롤이 로드되었는지 확인
        if (typeof tab1 === 'undefined' || !tab1.addTab) {
            console.warn("⚠️ 탭 컨트롤이 로드되지 않았습니다. 탭 추가를 건너뜁니다.");
            return;
        }

        // 이미 존재하는 탭인지 확인
        try {
            var existingTab = await tab1.getTab(tabId);
            if (existingTab) {
                console.log("이미 존재하는 탭:", tabId);
                await scwin.activateTab(tabId);
                return;
            }
        } catch (e) {
            // getTab에서 오류가 발생하면 탭이 존재하지 않는 것으로 간주
            console.log("탭이 존재하지 않습니다:", tabId);
        }

        // 탭 URL 정보를 내부 저장소에 저장
        if (!scwin.tabUrlMap) {
            scwin.tabUrlMap = {};
        }
        scwin.tabUrlMap[tabId] = tabUrl;
        console.log("탭 URL 정보 저장:", tabId, "->", tabUrl);

        // 웹스퀘어 공식 API를 사용하여 탭 추가
        var newTabId = await tab1.addTab(tabId, {
            "label": tabTitle,
            "title": tabTitle,
            "openAction": "exist",
            "closable": "true"
        }, {
            "src": tabUrl,
            "frameMode": "wframePreload",
            "scope": true,
            "alwaysDraw": "false"
        });

        // 활성 탭으로 설정
        if (isActive) {
            await scwin.activateTab(tabId);
        }

        console.log("✅ 탭 추가 완료:", tabId);

        // 탭 추가 후 탭 요소에 데이터 속성 설정
        setTimeout(function() {
            var tabElements = document.querySelectorAll('.w2_tabControl_tab');
            tabElements.forEach(function(tabElement) {
                var tabText = tabElement.querySelector('.w2_tabControl_tab_text');
                if (tabText && tabText.textContent.trim() === tabTitle) {
                    tabElement.setAttribute('data-tab-id', tabId);
                    tabElement.setAttribute('data-tab-url', tabUrl);
                    console.log("탭 요소에 데이터 속성 설정:", tabId, tabUrl);
                }
            });
        }, 200);

        // 탭 추가 후 빈 탭들 정리
        setTimeout(function() {
            scwin.cleanupEmptyTabs();
        }, 100);

        // 탭 추가 후 DOM 이벤트 리스너 다시 설정
        setTimeout(function() {
            scwin.setupTabClickListeners();
        }, 500);

    } catch (e) {
        console.error("탭 추가 오류:", e);
    }
};

// 웹스퀘어 공식 API를 사용한 탭 활성화
scwin.activateTab = async function(tabId) {
    try {
        console.log("탭 활성화:", tabId);

        // 탭 컨트롤이 로드되었는지 확인
        if (typeof tab1 === 'undefined' || !tab1.setSelectedTabIndex) {
            console.warn("⚠️ 탭 컨트롤이 로드되지 않았습니다. 탭 활성화를 건너뜁니다.");
            return;
        }

        // 해당 탭을 활성화
        await tab1.setSelectedTabIndex(tabId);
        console.log("✅ 탭 활성화 완료:", tabId);

        // 부모 프레임에 활성 탭 정보 전달
        if (window.parent && window.parent.scwin) {
            window.parent.scwin.currentActiveTabId = tabId;
        }

    } catch (e) {
        console.error("탭 활성화 오류:", e);
    }
};

// 웹스퀘어 공식 API를 사용한 탭 닫기
scwin.closeTab = async function(tabId) {
    try {
        console.log("탭 닫기:", tabId);

        // 탭 컨트롤이 로드되었는지 확인
        if (typeof tab1 === 'undefined' || !tab1.removeTab) {
            console.warn("⚠️ 탭 컨트롤이 로드되지 않았습니다. 탭 닫기를 건너뜁니다.");
            return;
        }

        // 저장된 URL 정보 정리
        if (scwin.tabUrlMap && scwin.tabUrlMap[tabId]) {
            delete scwin.tabUrlMap[tabId];
            console.log("탭 URL 정보 삭제:", tabId);
        }

        // 탭 제거
        await tab1.removeTab(tabId);
        console.log("✅ 탭 닫기 완료:", tabId);

    } catch (e) {
        console.error("탭 닫기 오류:", e);
    }
};

// 탭 ID로 탭 찾기
scwin.findTabById = async function(tabId) {
    try {
        // 탭 컨트롤이 로드되었는지 확인
        if (typeof tab1 === 'undefined' || !tab1.getTab) {
            console.warn("⚠️ 탭 컨트롤이 로드되지 않았습니다. 탭 찾기를 건너뜁니다.");
            return null;
        }

        var tab = await tab1.getTab(tabId);
        return tab;
    } catch (e) {
        console.error("탭 찾기 오류:", e);
        return null;
    }
};

// 탭 클릭 이벤트 (웹스퀘어 탭 컨트롤에서 자동 호출)
scwin.tab1_onclick = async function(tabId) {
    try {
        console.log("=== 탭 클릭 이벤트 시작 ===");
        console.log("클릭된 탭 ID:", tabId);
        console.log("현재 저장된 탭 URL 맵:", scwin.tabUrlMap);
        console.log("부모 프레임 존재:", !!window.parent);
        console.log("부모 프레임 scwin 존재:", !!(window.parent && window.parent.scwin));

        // 탭 ID가 없으면 기본값 설정
        if (!tabId) {
            console.warn("탭 ID가 없어서 기본값 사용");
            tabId = "menu_duty_manage";
        }

        // 1. 저장된 URL 정보 확인 (우선순위 1)
        var savedUrl = null;
        if (scwin.tabUrlMap && scwin.tabUrlMap[tabId]) {
            savedUrl = scwin.tabUrlMap[tabId];
            console.log("✅ 저장된 URL 정보 사용:", savedUrl);
        } else {
            console.log("❌ 저장된 URL 정보 없음");
        }

        // 2. 탭 정보에서 URL 가져오기 (우선순위 2)
        var tabInfo = null;
        var tabUrl = null;
        if (!savedUrl) {
            console.log("탭 정보에서 URL 찾기 시도...");
            try {
                tabInfo = await scwin.findTabById(tabId);
                console.log("탭 정보:", tabInfo);
                if (tabInfo && tabInfo.src) {
                    tabUrl = tabInfo.src;
                    console.log("✅ 탭 정보에서 URL 찾음:", tabUrl);
                } else {
                    console.log("❌ 탭 정보에서 URL 찾을 수 없음");
                }
            } catch (e) {
                console.log("탭 정보 조회 실패:", e);
            }
        }

        // 3. URL 추정 (우선순위 3)
        var estimatedUrl = null;
        if (!savedUrl && !tabUrl) {
            console.log("URL 추정 시도...");
            estimatedUrl = scwin.getUrlFromTabId(tabId);
            console.log("추정된 URL:", estimatedUrl);
        }

        // 4. 최종 URL 결정
        var finalUrl = savedUrl || tabUrl || estimatedUrl;
        console.log("최종 결정된 URL:", finalUrl);

        // 5. 부모 프레임에 메인 콘텐츠 변경 요청
        if (window.parent && window.parent.scwin && finalUrl) {
            console.log("부모 프레임에 메인 콘텐츠 변경 요청:", finalUrl);

            // 부모 프레임의 함수 호출
            if (window.parent.scwin.changeMainContentFromTab) {
                try {
                    window.parent.scwin.changeMainContentFromTab(finalUrl);
                    console.log("✅ 메인 콘텐츠 변경 요청 완료");
                } catch (e) {
                    console.error("메인 콘텐츠 변경 함수 호출 실패:", e);
                }
            } else {
                console.error("❌ 부모 프레임에 changeMainContentFromTab 함수가 없음");

                // 대체 방법: 직접 메인 콘텐츠 변경
                try {
                    if (window.parent.wfr_mainContent && window.parent.wfr_mainContent.setSrc) {
                        window.parent.wfr_mainContent.setSrc(finalUrl);
                        console.log("✅ 대체 방법으로 메인 콘텐츠 변경 완료");
                    }
                } catch (e) {
                    console.error("대체 방법도 실패:", e);
                }
            }

            // 사이드메뉴 활성 메뉴도 업데이트
            var menuId = scwin.getMenuIdFromTabId(tabId);
            if (menuId && window.parent.scwin.updateSideMenuActiveMenuWithRetry) {
                console.log("사이드메뉴 활성 메뉴 업데이트:", menuId);
                try {
                    window.parent.scwin.updateSideMenuActiveMenuWithRetry(menuId, 0);
                } catch (e) {
                    console.error("사이드메뉴 업데이트 실패:", e);
                }
            }
        } else {
            console.error("❌ URL을 찾을 수 없거나 부모 프레임이 없음:", {
                tabId: tabId,
                finalUrl: finalUrl,
                hasParent: !!window.parent,
                hasParentScwin: !!(window.parent && window.parent.scwin)
            });
        }

        console.log("=== 탭 클릭 이벤트 종료 ===");

    } catch (e) {
        console.error("❌ 탭 클릭 처리 오류:", e);
        console.error("오류 스택:", e.stack);
    }
};

// 탭 닫기 이벤트 (웹스퀘어 탭 컨트롤에서 자동 호출)
scwin.tab1_onclose = async function(tabId) {
    try {
        console.log("탭 닫기:", tabId);

        // 부모 프레임에 탭 닫기 알림
        if (window.parent && window.parent.scwin) {
            window.parent.scwin.onTabClosed(tabId);
        }

    } catch (e) {
        console.error("탭 닫기 처리 오류:", e);
    }
};

// 탭 ID로 메뉴 ID를 찾는 함수
scwin.getMenuIdFromTabId = function(tabId) {
    // 탭 ID와 메뉴 ID가 동일한 경우가 많음
    return tabId;
};

// 탭 ID로 URL을 추정하는 함수
scwin.getUrlFromTabId = function(tabId) {
    var tabUrlMap = {
        'menu_duty_manage': '/InsWebApp/ui/nurse/NurseManage.xml',
        'menu_schedule_manage': '/InsWebApp/ui/schedule/makeDuty.xml',
        'menu_my_schedule': '/InsWebApp/ui/duty/myschedule.xml',
        'menu_work_input': '/InsWebApp/ui/duty/HopeWorkEntry.xml',
    };

    return tabUrlMap[tabId] || null;
};
