// 다건 추가 버튼 - click 할 때마다 row 추가
scwin.btn_add_onclick = function (e) {
    var rowIdx = dlt_deptVoList.insertRow(); // 맨 마지막 row에 추가됨, 해당 rowIndex 반환

    // 신규 입력시 deptno를 입력하도록 변경하기 - cell의 readOnly를 false로 변경(gridView의 method 활용)
    grd_list.setCellReadOnly(rowIdx, "deptno", false);
};

