// 다중 삭제
scwin.btn_deleteRows_onclick = function (e) {
    let chkArrIdx = grd_list.getCheckedIndex("chk");
    dlt_deptVoList.deleteRows(chkArrIdx);
};

scwin.sbm_deleteTest_submitdone = function (e) {
