    scwin.btn_search_onclick();
};

// 통합 저장
scwin.btn_saveAll_onclick = function (e) {
    // 다건 신규입력, 수정, 삭제 한꺼번에 처리
    let reqObj = { "deptVoList": { "deptVoList": dlt_deptVoList.getModifiedJSON() } };
    $c.sbm.execute(sbm_modifyTest, reqObj); // 다건 삭제
};

// 통합 저장 후 재조회
scwin.sbm_modifyTest_submitdone = function (e) {
    // 재조회
    scwin.btn_search_onclick();
};
