var scwin = {};

scwin.onpageload = function(e) {
    console.log("âœ… ê°„í˜¸ì‚¬ ê´€ë¦¬ í˜ì´ì§€ ë¡œë“œ ì‹œì‘");

    // í˜ì´ì§€ ì´ˆê¸° ì„¤ì •
    dmp_nurseVo.set("pageIndex", 1);
    dmp_nurseVo.set("pageSize", 10);

    // í¼ ì´ˆê¸°í™”
    scwin.fn_clearForm();

    // í˜„ì¬ ë‚ ì§œ ì„¤ì •
    scwin.fn_setCurrentDate();

    // ê°„í˜¸ì‚¬ ëª©ë¡ ì¡°íšŒ
