// ê°„í˜¸ì‚¬ ëª©ë¡ ì¡°íšŒ ì™„ë£Œ
scwin.sbm_selectNurseList_submitdone = function(e) {
    try {
        var result = e.responseJSON;
        console.log("ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½ ì„œë²„ ì‘ë‹µ:", result);

        if (result && result.nurseVoList) {
            // ì²´í¬ë°•ìŠ¤ ì´ˆê¸°í™”ë¥¼ ìœ„í•´ chk_select í•„ë“œ ì¶”ê°€
            result.nurseVoList.forEach(function(item, index) {
                item.chk_select = "0";
                // ê²½ë ¥ í•„ë“œ ì²˜ë¦¬ (ìˆ«ì -> ë¬¸ìì—´)
                if (typeof item.carrer === 'number') {
                    item.carrer = item.carrer + "ë…„";
                }
            });

            // ë°ì´í„° ë¡œë“œ
            dlt_nurseList.setJSON(result.nurseVoList);

            console.log("âœ… ê°„í˜¸ì‚¬ ëª©ë¡ ì¡°íšŒ ì™„ë£Œ:", result.nurseVoList.length + "ê±´");
        } else {
            console.log("âš ï¸ ì¡°íšŒëœ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.");
            dlt_nurseList.removeAll();
        }

        // ì²´í¬ë°•ìŠ¤ ì´ˆê¸°í™”
        scwin.fn_clearAllChecks();

    } catch (error) {
        console.error("âŒ ëª©ë¡ ì¡°íšŒ ì˜¤ë¥˜:", error);
        alert("ë°ì´í„° ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.\n" + error.message);
    }
};

