// ì„ íƒëœ í–‰ ê°€ì ¸ì˜¤ê¸°
scwin.fn_getSelectedRows = function() {
    var selectedRows = [];
    var rowCount = dlt_nurseList.getRowCount();

    for (var i = 0; i < rowCount; i++) {
        var isChecked = dlt_nurseList.getCellData(i, "chk_select");
        if (isChecked === "1" || isChecked === true || isChecked === "true") {
            selectedRows.push(i);
        }
    }

    return selectedRows;
};

// ëª¨ë“  ì²´í¬ë°•ìŠ¤ í•´ì œ
scwin.fn_clearAllChecks = function() {
    var rowCount = dlt_nurseList.getRowCount();
    for (var i = 0; i < rowCount; i++) {
        dlt_nurseList.setCellData(i, "chk_select", false);
    }
};

// ê·¸ë¦¬ë“œ í–‰ í´ë¦­
scwin.grd_nurseList_oncellclick = function(row, col) {
    // ì²´í¬ë°•ìŠ¤ ì»¬ëŸ¼ì´ ì•„ë‹Œ ê²½ìš°ì—ë§Œ ìƒì„¸ ì •ë³´ ë¡œë“œ
    if (col !== 0) {
        var nurseId = dlt_nurseList.getCellData(row, "nurseId");
        if (nurseId) {
            scwin.fn_selectNurse(nurseId);
            console.log("ì„ íƒëœ ê°„í˜¸ì‚¬ ID:", nurseId);
        }
    }
};

// í¼ ê²€ì¦
scwin.fn_validateForm = function() {
    var empno = dmp_nurseVo.get("empno");
    var position = dmp_nurseVo.get("position");

    if (!empno || empno.toString().trim() === "") {
        alert("ì‚¬ì›ë²ˆí˜¸ë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš”.");
        if (ipt_empno) ipt_empno.focus();
        return false;
    }

    if (!position || position.trim() === "") {
        alert("ì§ê¸‰ì„ ì…ë ¥í•´ì£¼ì„¸ìš”.");
        if (ipt_position) ipt_position.focus();
        return false;
    }

    return true;
};

// ì‚¬ì›ë²ˆí˜¸ ë³€ê²½ ì´ë²¤íŠ¸
scwin.ipt_empno_onchange = function() {
    var empno = dmp_nurseVo.get("empno");

    if (empno && empno.toString().trim() !== "") {
        scwin.fn_checkEmployee(empno);
    } else {
        // ì‚¬ì›ë²ˆí˜¸ê°€ ì—†ìœ¼ë©´ ê·¼ë¬´ìëª… í•„ë“œ ì´ˆê¸°í™”
        if (txt_nurseName_display) {
            txt_nurseName_display.setValue("");
        }
        dmp_nurseVo.set("nurseName", "");
    }
};

// ì‚¬ì› ì •ë³´ í™•ì¸
scwin.fn_checkEmployee = function(empno) {
    // EMP í…Œì´ë¸”ì—ì„œ ì‚¬ì› ì •ë³´ ì¡°íšŒ (ê°„ë‹¨í•œ í™•ì¸ìš©)
    // ì‹¤ì œë¡œëŠ” ë³„ë„ì˜ APIë¥¼ ë§Œë“¤ì–´ì•¼ í•˜ì§€ë§Œ, ì—¬ê¸°ì„œëŠ” ê¸°ì¡´ ë°ì´í„°ë¥¼ í™œìš©
    console.log("ì‚¬ì›ë²ˆí˜¸ í™•ì¸:", empno);

    // ìƒ˜í”Œ ë°ì´í„°ë¡œ í™•ì¸ (ì‹¤ì œë¡œëŠ” ì„œë²„ì—ì„œ ì¡°íšŒ)
    var sampleEmployees = {
        "7521": "WARD",
        "7698": "BLAKE", 
        "7782": "CLARK",
        "7788": "SCOTT",
        "7839": "KING",
        "7844": "TURNER",
        "7876": "ADAMS",
        "7900": "JAMES",
        "7902": "FORD",
        "7934": "MILLER"
    };

    var empName = sampleEmployees[empno.toString()];
    if (empName) {
        if (txt_nurseName_display) {
            txt_nurseName_display.setValue(empName);
        }
        dmp_nurseVo.set("nurseName", empName);
        console.log("âœ… ì‚¬ì› í™•ì¸ë¨:", empno, empName);
    } else {
        if (txt_nurseName_display) {
            txt_nurseName_display.setValue("âš ï¸ ì‚¬ì›ë²ˆí˜¸ë¥¼ í™•ì¸í•´ì£¼ì„¸ìš”");
        }
        dmp_nurseVo.set("nurseName", "");
        console.warn("âš ï¸ ì¡´ì¬í•˜ì§€ ì•ŠëŠ” ì‚¬ì›ë²ˆí˜¸:", empno);
    }
};

