    console.log("ì„œë²„ ì—°ë™ ëª¨ë“œ - í˜„ì¬ëŠ” ë”ë¯¸ ë°ì´í„° ì‚¬ìš©");
};

// ê°„í˜¸ì‚¬ ìƒì„¸ ì¡°íšŒ
scwin.fn_selectNurse = function(nurseId) {
    var nurseInfo = scwin.nurseData.find(function(item) {
        return item.nurseId === nurseId && item.delYn !== "Y";
    });

    if (nurseInfo) {
        dmp_nurseVo.setJSON(nurseInfo);
        console.log("ê°„í˜¸ì‚¬ ì •ë³´ ì¡°íšŒ ì™„ë£Œ:", nurseInfo.nurseName);
    } else {
        alert("ê°„í˜¸ì‚¬ ì •ë³´ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
    }
};

