// ì‚­ì œ ë²„íŠ¼ í´ë¦­
scwin.btn_delete_onclick = function() {
    var nurseId = dmp_nurseVo.get("nurseId");
    if (!nurseId) {
        alert("ì‚­ì œí•  ê°„í˜¸ì‚¬ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.");
        return;
    }

    if (confirm("ì„ íƒí•œ ê°„í˜¸ì‚¬ ì •ë³´ë¥¼ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?")) {
        if (scwin.fn_deleteNurse(nurseId)) {
            alert("ê°„í˜¸ì‚¬ ì •ë³´ê°€ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤.");
            scwin.fn_clearForm();
            scwin.fn_selectNurseList();
        }
    }
};

// ë‹¤ì¤‘ ì‚­ì œ ë²„íŠ¼ í´ë¦­
scwin.btn_delete_multi_onclick = function() {
    var selectedRows = scwin.fn_getSelectedRows();

    if (selectedRows.length === 0) {
        alert("ì‚­ì œí•  ê°„í˜¸ì‚¬ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.");
        return;
    }

    if (confirm("ì„ íƒí•œ " + selectedRows.length + "ê±´ì˜ ê°„í˜¸ì‚¬ ì •ë³´ë¥¼ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?")) {
        var deletedCount = 0;

        selectedRows.forEach(function(row) {
            var nurseId = dlt_nurseList.getCellData(row, "nurseId");
            if (scwin.fn_deleteNurse(nurseId)) {
                deletedCount++;
            }
        });

        alert(deletedCount + "ê±´ì˜ ê°„í˜¸ì‚¬ ì •ë³´ê°€ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤.");
        scwin.fn_clearForm();
        scwin.fn_selectNurseList();
    }
};

// ì„ íƒëœ í–‰ ê°€ì ¸ì˜¤ê¸°
scwin.fn_getSelectedRows = function() {
    var selectedRows = [];
    var rowCount = dlt_nurseList.getRowCount();

    for (var i = 0; i < rowCount; i++) {
        var isChecked = dlt_nurseList.getCellData(i, "chk_select");
        if (isChecked === "1" || isChecked === true) {
            selectedRows.push(i);
        }
    }

    return selectedRows;
};

// ëª¨ë“  ì²´í¬ë°•ìŠ¤ í•´ì œ
scwin.fn_clearAllChecks = function() {
    var rowCount = dlt_nurseList.getRowCount();
    for (var i = 0; i < rowCount; i++) {
        dlt_nurseList.setCellData(i, "chk_select", "0");
    }
};

// ê·¸ë¦¬ë“œ í–‰ í´ë¦­
scwin.grd_nurseList_oncellclick = function(row, col) {
    // ì²´í¬ë°•ìŠ¤ ì»¬ëŸ¼ í´ë¦­ ì‹œ ì²´í¬ ìƒíƒœ í† ê¸€
    if (col === 0) {
        var currentValue = dlt_nurseList.getCellData(row, "chk_select");
        var newValue = (currentValue === "1" || currentValue === true) ? "0" : "1";
        dlt_nurseList.setCellData(row, "chk_select", newValue);
        return;
    }

    // ë‹¤ë¥¸ ì»¬ëŸ¼ í´ë¦­ ì‹œ ìƒì„¸ ì •ë³´ ë¡œë“œ
    var nurseId = dlt_nurseList.getCellData(row, "nurseId");
    if (nurseId) {
        scwin.fn_selectNurse(nurseId);
    }
};

// í¼ ê²€ì¦
scwin.fn_validateForm = function() {
    var nurseName = dmp_nurseVo.get("nurseName");
    var position = dmp_nurseVo.get("position");

    if (!nurseName || nurseName.trim() === "") {
        alert("ê°„í˜¸ì‚¬ëª…ì„ ì…ë ¥í•´ì£¼ì„¸ìš”.");
        ipt_nurseName.focus();
        return false;
    }

    if (nurseName.trim().length < 2) {
        alert("ê°„í˜¸ì‚¬ëª…ì€ 2ê¸€ì ì´ìƒ ì…ë ¥í•´ì£¼ì„¸ìš”.");
        ipt_nurseName.focus();
        return false;
    }

    if (!position || position.trim() === "") {
        alert("ì§ê¸‰ì„ ì…ë ¥í•´ì£¼ì„¸ìš”.");
        ipt_position.focus();
        return false;
    }

    // ì¤‘ë³µ ê²€ì‚¬ (ìˆ˜ì • ì‹œì—ëŠ” ë³¸ì¸ ì œì™¸)
    var nurseId = dmp_nurseVo.get("nurseId");
    var duplicateNurse = scwin.nurseData.find(function(item) {
        return item.nurseName === nurseName.trim() && 
               item.delYn !== "Y" && 
               item.nurseId !== nurseId;
    });

    if (duplicateNurse) {
        alert("ì´ë¯¸ ë“±ë¡ëœ ê°„í˜¸ì‚¬ëª…ì…ë‹ˆë‹¤.");
        ipt_nurseName.focus();
        return false;
    }

    return true;
};

// í¼ ì´ˆê¸°í™”
scwin.fn_clearForm = function() {
    dmp_nurseVo.set("nurseId", "");
    dmp_nurseVo.set("nurseName", "");
    dmp_nurseVo.set("position", "");
    dmp_nurseVo.set("career", "");
    dmp_nurseVo.set("specialNote", "");
    dmp_nurseVo.set("scNurseName", "");
    dmp_nurseVo.set("scPosition", "");
};

// í˜„ì¬ ë‚ ì§œ ë°˜í™˜
scwin.fn_getCurrentDate = function() {
    var now = new Date();
    var year = now.getFullYear();
    var month = String(now.getMonth() + 1).padStart(2, '0');
    var day = String(now.getDate()).padStart(2, '0');
    return year + "-" + month + "-" + day;
};

// ì—‘ì…€ ì—…ë¡œë“œ
scwin.btn_excel_onclick = function() {
    try {
        var jsnOptions = {
            headerExist: "1", // Excel íŒŒì¼ì— í—¤ë”ê°€ ìˆëŠ”ì§€ ì—¬ë¶€
            uploadDataCallBack: scwin.fn_excelUploadCallback // ì—…ë¡œë“œ ì™„ë£Œ í›„ ì½œë°±
        };

        // WebSquareì˜ ì—‘ì…€ ì—…ë¡œë“œ ê¸°ëŠ¥ ì‚¬ìš©
        if (grd_nurseList.advancedExcelUpload) {
            grd_nurseList.advancedExcelUpload(jsnOptions);
        } else {
            // ëŒ€ì²´ ë°©ë²•: íŒŒì¼ ì—…ë¡œë“œ ì‹œë®¬ë ˆì´ì…˜
            scwin.fn_simulateExcelUpload();
        }
    } catch (error) {
        console.error("ì—‘ì…€ ì—…ë¡œë“œ ì˜¤ë¥˜:", error);
        scwin.fn_simulateExcelUpload();
    }
};

// ì—‘ì…€ ì—…ë¡œë“œ ì‹œë®¬ë ˆì´ì…˜ (ì‹¤ì œ ê¸°ëŠ¥ì´ ì—†ì„ ê²½ìš°)
scwin.fn_simulateExcelUpload = function() {
    if (confirm("ì—‘ì…€ ì—…ë¡œë“œ ê¸°ëŠ¥ì„ ì‹œë®¬ë ˆì´ì…˜í•˜ì‹œê² ìŠµë‹ˆê¹Œ?\nìƒ˜í”Œ ë°ì´í„° 3ê±´ì´ ì¶”ê°€ë©ë‹ˆë‹¤.")) {
        var sampleUploadData = [
            {
                nurseName: "ì—‘ì…€ì—…ë¡œë“œ_ê¹€ì˜í¬",
                position: "ê°„í˜¸ì‚¬", 
                career: "1ë…„",
                specialNote: "ì—‘ì…€ë¡œ ì—…ë¡œë“œëœ ë°ì´í„°"
            },
            {
                nurseName: "ì—‘ì…€ì—…ë¡œë“œ_ì´ì² ìˆ˜",
                position: "ì£¼ì„ê°„í˜¸ì‚¬",
                career: "6ë…„", 
                specialNote: "ì—‘ì…€ë¡œ ì—…ë¡œë“œëœ ë°ì´í„°"
            },
            {
                nurseName: "ì—‘ì…€ì—…ë¡œë“œ_ë°•ìˆ˜ì§„",
                position: "ê°„í˜¸ì‚¬",
                career: "2ë…„",
                specialNote: "ì—‘ì…€ë¡œ ì—…ë¡œë“œëœ ë°ì´í„°"
            }
        ];

        sampleUploadData.forEach(function(data) {
            var newNurse = {
                chk_select: "0",
                nurseId: "N" + String(scwin.nurseCounter).padStart(3, '0'),
                nurseName: data.nurseName,
                position: data.position,
                career: data.career,
                specialNote: data.specialNote,
                regDate: scwin.fn_getCurrentDate(),
                modDate: scwin.fn_getCurrentDate(),
                useYn: "Y",
                delYn: "N"
            };

            scwin.nurseData.push(newNurse);
            scwin.nurseCounter++;
        });

        alert("ì—‘ì…€ ì—…ë¡œë“œ ì™„ë£Œ: " + sampleUploadData.length + "ê±´ì˜ ë°ì´í„°ê°€ ì¶”ê°€ë˜ì—ˆìŠµë‹ˆë‹¤.");
        scwin.fn_selectNurseList();
    }
};

// ì—‘ì…€ ì—…ë¡œë“œ ì™„ë£Œ ì½œë°±
scwin.fn_excelUploadCallback = function(uploadData) {
    if (uploadData && uploadData.length > 0) {
        var addedCount = 0;

        uploadData.forEach(function(data) {
            // í•„ìˆ˜ í•„ë“œ ê²€ì¦
            if (data.nurseName && data.position) {
                var newNurse = {
                    chk_select: "0",
                    nurseId: "N" + String(scwin.nurseCounter).padStart(3, '0'),
                    nurseName: data.nurseName,
                    position: data.position,
                    career: data.career || "",
                    specialNote: data.specialNote || "",
                    regDate: scwin.fn_getCurrentDate(),
                    modDate: scwin.fn_getCurrentDate(),
                    useYn: "Y",
                    delYn: "N"
                };

                scwin.nurseData.push(newNurse);
                scwin.nurseCounter++;
                addedCount++;
            }
        });

        alert("ì—‘ì…€ ì—…ë¡œë“œ ì™„ë£Œ: " + addedCount + "ê±´ì˜ ê°„í˜¸ì‚¬ ì •ë³´ê°€ ë“±ë¡ë˜ì—ˆìŠµë‹ˆë‹¤.");
        scwin.fn_selectNurseList();
    } else {
        alert("ì—…ë¡œë“œí•  ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.");
    }
};
