head.script:

// scwin 객체 초기화
var scwin = {};

scwin.onpageload = function(e) {
    console.log("✅ 간호사 관리 페이지 로드 시작");

    // 페이지 초기 설정
    dmp_nurseVo.set("pageIndex", 1);
    dmp_nurseVo.set("pageSize", 10);

    // 폼 초기화
    scwin.fn_clearForm();

    // 현재 날짜 설정
    scwin.fn_setCurrentDate();

    // 간호사 목록 조회
    scwin.fn_selectNurseList();

    console.log("✅ 간호사 관리 페이지 로드 완료");
};

// 현재 날짜 설정
scwin.fn_setCurrentDate = function() {
    var now = new Date();
    var year = now.getFullYear();
    var month = String(now.getMonth() + 1).padStart(2, '0');
    var day = String(now.getDate()).padStart(2, '0');
    var currentDate = year + "-" + month + "-" + day;

    if (txt_currentDate) {
        txt_currentDate.setValue(currentDate);
    }
};

// 간호사 목록 조회
scwin.fn_selectNurseList = function() {
    console.log("������ 간호사 목록 조회 시작");

    // 검색 조건 설정
    var searchData = {
        scNurseName: dmp_nurseVo.get("scNurseName") || "",
        scPosition: dmp_nurseVo.get("scPosition") || "",
        pageIndex: dmp_nurseVo.get("pageIndex") || 1,
        pageSize: dmp_nurseVo.get("pageSize") || 10
    };

    console.log("검색 조건:", searchData);

    // 서버 API 호출을 위한 submission 실행
    dmp_nurseVo.setJSON(searchData);
    sbm_selectNurseList.submit();
};

// 간호사 목록 조회 완료
scwin.sbm_selectNurseList_submitdone = function(e) {
    try {
        var result = e.responseJSON;
        console.log("������ 서버 응답:", result);

        if (result && result.nurseVoList) {
            // 체크박스 초기화를 위해 chk_select 필드 추가
            result.nurseVoList.forEach(function(item, index) {
                item.chk_select = "0";
                // 경력 필드 처리 (숫자 -> 문자열)
                if (typeof item.carrer === 'number') {
                    item.carrer = item.carrer + "년";
                }
            });

            // 데이터 로드
            dlt_nurseList.setJSON(result.nurseVoList);

            console.log("✅ 간호사 목록 조회 완료:", result.nurseVoList.length + "건");
        } else {
            console.log("⚠️ 조회된 데이터가 없습니다.");
            dlt_nurseList.removeAll();
        }

        // 체크박스 초기화
        scwin.fn_clearAllChecks();

    } catch (error) {
        console.error("❌ 목록 조회 오류:", error);
        alert("데이터 조회 중 오류가 발생했습니다.\n" + error.message);
    }
};

// 간호사 상세 조회
scwin.fn_selectNurse = function(nurseId) {
    if (!nurseId) {
        console.warn("⚠️ 간호사 ID가 없습니다.");
        return;
    }

    console.log("������ 간호사 상세 조회:", nurseId);

    dmp_nurseVo.set("nurseId", nurseId);
    sbm_selectNurse.submit();
};

// 간호사 상세 조회 완료
scwin.sbm_selectNurse_submitdone = function(e) {
    try {
        var result = e.responseJSON;
        console.log("������ 간호사 상세 정보:", result);

        if (result && result.nurseVo) {
            // 폼에 데이터 로드
            dmp_nurseVo.setJSON(result.nurseVo);

            // 근무자명 표시 필드 업데이트
            if (txt_nurseName_display && result.nurseVo.nurseName) {
                txt_nurseName_display.setValue(result.nurseVo.nurseName);
            }

            console.log("✅ 간호사 상세 조회 완료:", result.nurseVo.nurseName);
        } else {
            console.warn("⚠️ 간호사 정보를 찾을 수 없습니다.");
            alert("간호사 정보를 찾을 수 없습니다.");
        }
    } catch (error) {
        console.error("❌ 상세 조회 오류:", error);
        alert("간호사 정보 조회 중 오류가 발생했습니다.\n" + error.message);
    }
};

// 간호사 등록
scwin.fn_insertNurse = function() {
    console.log("➕ 간호사 등록 시작");

    if (!scwin.fn_validateForm()) {
        return;
    }

    // 사원번호 확인
    var empno = dmp_nurseVo.get("empno");
    var nurseName = dmp_nurseVo.get("nurseName");

    if (!nurseName || nurseName.indexOf("⚠️") >= 0) {
        alert("올바른 사원번호를 입력해주세요.");
        if (ipt_empno) ipt_empno.focus();
        return;
    }

    // 경력 필드 처리 (문자열 -> 숫자)
    var carrer = dmp_nurseVo.get("carrer");
    if (carrer && typeof carrer === 'string') {
        carrer = parseInt(carrer.replace(/[^0-9]/g, '')) || 0;
        dmp_nurseVo.set("carrer", carrer);
    }

    // 현재 날짜 설정
    var now = new Date();
    var currentDate = now.getFullYear() + "-" + String(now.getMonth() + 1).padStart(2, '0') + "-" + String(now.getDate()).padStart(2, '0');
    dmp_nurseVo.set("createdAt", currentDate);
    dmp_nurseVo.set("updatedAt", currentDate);

    sbm_insertNurse.submit();
};

// 간호사 등록 완료
scwin.sbm_insertNurse_submitdone = function(e) {
    try {
        var result = e.responseText || e.responseJSON;
        console.log("✅ 간호사 등록 결과:", result);

        alert("간호사 정보가 등록되었습니다.");
        scwin.fn_clearForm();
        scwin.fn_selectNurseList();

    } catch (error) {
        console.error("❌ 등록 오류:", error);
        alert("간호사 등록 중 오류가 발생했습니다.\n" + error.message);
    }
};

// 간호사 수정
scwin.fn_updateNurse = function() {
    console.log("✏️ 간호사 수정 시작");

    var nurseId = dmp_nurseVo.get("nurseId");
    if (!nurseId) {
        alert("수정할 간호사를 선택해주세요.");
        return;
    }

    if (!scwin.fn_validateForm()) {
        return;
    }

    // 사원번호 확인
    var nurseName = dmp_nurseVo.get("nurseName");
    if (!nurseName || nurseName.indexOf("⚠️") >= 0) {
        alert("올바른 사원번호를 입력해주세요.");
        if (ipt_empno) ipt_empno.focus();
        return;
    }

    // 경력 필드 처리 (문자열 -> 숫자)
    var carrer = dmp_nurseVo.get("carrer");
    if (carrer && typeof carrer === 'string') {
        carrer = parseInt(carrer.replace(/[^0-9]/g, '')) || 0;
        dmp_nurseVo.set("carrer", carrer);
    }

    // 수정 날짜 설정
    var now = new Date();
    var currentDate = now.getFullYear() + "-" + String(now.getMonth() + 1).padStart(2, '0') + "-" + String(now.getDate()).padStart(2, '0');
    dmp_nurseVo.set("updatedAt", currentDate);

    sbm_updateNurse.submit();
};

// 간호사 수정 완료
scwin.sbm_updateNurse_submitdone = function(e) {
    try {
        var result = e.responseText || e.responseJSON;
        console.log("✅ 간호사 수정 결과:", result);

        alert("간호사 정보가 수정되었습니다.");
        scwin.fn_selectNurseList();

    } catch (error) {
        console.error("❌ 수정 오류:", error);
        alert("간호사 수정 중 오류가 발생했습니다.\n" + error.message);
    }
};

// 간호사 삭제
scwin.fn_deleteNurse = function() {
    console.log("������️ 간호사 삭제 시작");

    var nurseId = dmp_nurseVo.get("nurseId");
    var nurseName = dmp_nurseVo.get("nurseName");

    if (!nurseId) {
        alert("삭제할 간호사를 선택해주세요.");
        return;
    }

    if (confirm("선택한 간호사 정보를 삭제하시겠습니까?\n간호사명: " + nurseName)) {
        sbm_deleteNurse.submit();
    }
};

// 간호사 삭제 완료
scwin.sbm_deleteNurse_submitdone = function(e) {
    try {
        var result = e.responseText || e.responseJSON;
        console.log("✅ 간호사 삭제 결과:", result);

        alert("간호사 정보가 삭제되었습니다.");
        scwin.fn_clearForm();
        scwin.fn_selectNurseList();

    } catch (error) {
        console.error("❌ 삭제 오류:", error);
        alert("간호사 삭제 중 오류가 발생했습니다.\n" + error.message);
    }
};

// 검색 버튼 클릭
scwin.btn_search_onclick = function() {
    var scNurseName = dmp_nurseVo.get("scNurseName") || "";
    var scPosition = dmp_nurseVo.get("scPosition") || "";

    if (scNurseName.trim() === "" && scPosition.trim() === "") {
        alert("검색 조건을 입력해주세요.");
        return;
    }

    dmp_nurseVo.set("pageIndex", 1);
    scwin.fn_selectNurseList();
};

// 전체조회 버튼 클릭
scwin.btn_list_all_onclick = function() {
    dmp_nurseVo.set("scNurseName", "");
    dmp_nurseVo.set("scPosition", "");
    dmp_nurseVo.set("pageIndex", 1);

    scwin.fn_selectNurseList();
    console.log("✅ 전체조회 실행");
};

// 초기화 버튼 클릭
scwin.btn_reset_onclick = function() {
    scwin.fn_clearForm();
    console.log("������ 폼 초기화 완료");
};

// 등록 버튼 클릭
scwin.btn_insert_onclick = function() {
    scwin.fn_insertNurse();
};

// 수정 버튼 클릭
scwin.btn_update_onclick = function() {
    scwin.fn_updateNurse();
};

// 삭제 버튼 클릭
scwin.btn_delete_onclick = function() {
    scwin.fn_deleteNurse();
};

// 전체선택 버튼 클릭
scwin.btn_select_all_onclick = function() {
    var rowCount = dlt_nurseList.getRowCount();

    if (rowCount === 0) {
        alert("표시할 데이터가 없습니다.");
        return;
    }

    var allSelected = true;

    // 모든 행이 선택되었는지 확인
    for (var i = 0; i < rowCount; i++) {
        var isChecked = dlt_nurseList.getCellData(i, "chk_select");
        if (isChecked !== "1" && isChecked !== true && isChecked !== "true") {
            allSelected = false;
            break;
        }
    }

    // 모든 행이 선택되었으면 해제, 아니면 모두 선택
    var newValue = allSelected ? false : true;
    for (var i = 0; i < rowCount; i++) {
        dlt_nurseList.setCellData(i, "chk_select", newValue);
    }

    console.log((allSelected ? "전체 선택 해제" : "전체 선택") + " - " + rowCount + "행");
};

// 다중 삭제 버튼 클릭
scwin.btn_delete_multi_onclick = function() {
    var selectedRows = scwin.fn_getSelectedRows();

    if (selectedRows.length === 0) {
        alert("삭제할 간호사를 선택해주세요.");
        return;
    }

    if (confirm("선택한 " + selectedRows.length + "건의 간호사 정보를 삭제하시겠습니까?")) {
        var deletedCount = 0;
        var totalSelected = selectedRows.length;

        // 순차적으로 삭제 처리
        scwin.fn_deleteMultiNurses(selectedRows, 0, function() {
            alert("선택한 간호사 정보가 삭제되었습니다.");
            scwin.fn_clearForm();
            scwin.fn_selectNurseList();
        });
    }
};

// 다중 삭제 처리 (순차 실행)
scwin.fn_deleteMultiNurses = function(selectedRows, index, callback) {
    if (index >= selectedRows.length) {
        callback();
        return;
    }

    var row = selectedRows[index];
    var nurseId = dlt_nurseList.getCellData(row, "nurseId");

    if (nurseId) {
        dmp_nurseVo.set("nurseId", nurseId);

        // 삭제 완료 후 다음 삭제 실행
        scwin.deleteCallback = function() {
            setTimeout(function() {
                scwin.fn_deleteMultiNurses(selectedRows, index + 1, callback);
            }, 100);
        };

        sbm_deleteNurse.submit();
    } else {
        scwin.fn_deleteMultiNurses(selectedRows, index + 1, callback);
    }
};

// 엑셀 업로드 (더미 데이터)
scwin.btn_excel_onclick = function() {
    alert("엑셀 업로드 기능은 현재 데이터베이스 연동 버전에서는 지원하지 않습니다.\n대신 '등록' 버튼을 사용하여 개별 간호사 정보를 입력해주세요.");
};

// 다중 등록/수정 (더미)
scwin.btn_insert_multi_onclick = function() {
    alert("다중 등록 기능은 추후 구현 예정입니다.");
};

scwin.btn_update_multi_onclick = function() {
    alert("다중 수정 기능은 추후 구현 예정입니다.");
};

// 선택된 행 가져오기
scwin.fn_getSelectedRows = function() {
    var selectedRows = [];
    var rowCount = dlt_nurseList.getRowCount();

    for (var i = 0; i < rowCount; i++) {
        var isChecked = dlt_nurseList.getCellData(i, "chk_select");
        if (isChecked === "1" || isChecked === true || isChecked === "true") {
            selectedRows.push(i);
        }
    }

    return selectedRows;
};

// 모든 체크박스 해제
scwin.fn_clearAllChecks = function() {
    var rowCount = dlt_nurseList.getRowCount();
    for (var i = 0; i < rowCount; i++) {
        dlt_nurseList.setCellData(i, "chk_select", false);
    }
};

// 그리드 행 클릭
scwin.grd_nurseList_oncellclick = function(row, col) {
    // 체크박스 컬럼이 아닌 경우에만 상세 정보 로드
    if (col !== 0) {
        var nurseId = dlt_nurseList.getCellData(row, "nurseId");
        if (nurseId) {
            scwin.fn_selectNurse(nurseId);
            console.log("선택된 간호사 ID:", nurseId);
        }
    }
};

// 폼 검증
scwin.fn_validateForm = function() {
    var empno = dmp_nurseVo.get("empno");
    var position = dmp_nurseVo.get("position");

    if (!empno || empno.toString().trim() === "") {
        alert("사원번호를 입력해주세요.");
        if (ipt_empno) ipt_empno.focus();
        return false;
    }

    if (!position || position.trim() === "") {
        alert("직급을 입력해주세요.");
        if (ipt_position) ipt_position.focus();
        return false;
    }

    return true;
};

// 사원번호 변경 이벤트
scwin.ipt_empno_onchange = function() {
    var empno = dmp_nurseVo.get("empno");

    if (empno && empno.toString().trim() !== "") {
        scwin.fn_checkEmployee(empno);
    } else {
        // 사원번호가 없으면 근무자명 필드 초기화
        if (txt_nurseName_display) {
            txt_nurseName_display.setValue("");
        }
        dmp_nurseVo.set("nurseName", "");
    }
};

// 사원 정보 확인
scwin.fn_checkEmployee = function(empno) {
    // EMP 테이블에서 사원 정보 조회 (간단한 확인용)
    // 실제로는 별도의 API를 만들어야 하지만, 여기서는 기존 데이터를 활용
    console.log("사원번호 확인:", empno);

    // 샘플 데이터로 확인 (실제로는 서버에서 조회)
    var sampleEmployees = {
        "7521": "WARD",
        "7698": "BLAKE", 
        "7782": "CLARK",
        "7788": "SCOTT",
        "7839": "KING",
        "7844": "TURNER",
        "7876": "ADAMS",
        "7900": "JAMES",
        "7902": "FORD",
        "7934": "MILLER"
    };

    var empName = sampleEmployees[empno.toString()];
    if (empName) {
        if (txt_nurseName_display) {
            txt_nurseName_display.setValue(empName);
        }
        dmp_nurseVo.set("nurseName", empName);
        console.log("✅ 사원 확인됨:", empno, empName);
    } else {
        if (txt_nurseName_display) {
            txt_nurseName_display.setValue("⚠️ 사원번호를 확인해주세요");
        }
        dmp_nurseVo.set("nurseName", "");
        console.warn("⚠️ 존재하지 않는 사원번호:", empno);
    }
};

// 폼 초기화
scwin.fn_clearForm = function() {
    dmp_nurseVo.set("nurseId", "");
    dmp_nurseVo.set("empno", "");
    dmp_nurseVo.set("nurseName", "");
    dmp_nurseVo.set("position", "");
    dmp_nurseVo.set("carrer", "");
    dmp_nurseVo.set("specialNote", "");
    dmp_nurseVo.set("hireDate", "");
    dmp_nurseVo.set("wardId", "");
    dmp_nurseVo.set("wardName", "");
    dmp_nurseVo.set("scNurseName", "");
    dmp_nurseVo.set("scPosition", "");

    // 근무자명 표시 필드 초기화
    if (txt_nurseName_display) {
        txt_nurseName_display.setValue("");
    }
};
